import React, { Component } from 'react';
import Corousel from '../../Components/Corousel/Corousel';
import PicCards from '../../Components/PicCards/PicCards';
import {Image} from 'react-bootstrap';
import Form from '../../Components/Form/Form';
import './Home.css'
import Fade from 'react-reveal/Fade';
import { Route,Redirect } from 'react-router-dom';
import About from '../About/Aboutus/About';
class Home extends Component {

	ContinuedHandler = () => {
    this.props.history.replace('/about');   //on clicking image path becomes....prev+'/parentSponsership' and hence according to the Route below it routes to that component
    }
  render() {
let summary = <Redirect to='/' /> //to enable directing to home page after reloading(refreshing) 
		{			// config summary iff ingredients have been fetched from firebase otherwise it redirects to home page
			summary=(
			<div>
			<PicCards Clicked={this.ContinuedHandler}/>
			<Route path={this.props.match.path + '/about'} 
			component={About}/> 
			</div>);

		}

    return (
      <div >     
        <Corousel/>
        <br/>
        <br/>
        
        <p className='capt'><Fade bottom delay={500}>Want to come to Canada to study, work, visit or live ?</Fade><Fade bottom delay={700}>You are at the right place</Fade> <Fade bottom delay={900}>We will help you with all your immigration needs.</Fade></p>
		<br/>
		{summary}
 
        
      </div>
    );
  }
}

export default Home;
