import React from "react";
import { MDBMask, MDBView, MDBContainer, MDBRow, MDBCol } from "mdbreact";
import './PicCards.css';
import hs3o from '../../Assets/tower.jpg';
import Spouse from '../../Assets/Spouse.jpg';
import img from '../../Assets/vertical.jpg';
import Fade from 'react-reveal/Fade';
import Parents from '../../Assets/Parents.jpg';
import Nominee from '../../Assets/Nominee.jpg';
import { NavLink } from 'react-router-dom';
import { Route,Redirect } from 'react-router-dom';
import About from '../../Container/About/Aboutus/About';

class PicCards extends React.Component {
  state={
    cards:[
    {img:Spouse,caption:" Family or spouse Sponsorship "},
    {img:Nominee,caption:" Provincial Nominee program "},
    {img:Parents,caption:" Parents and Grandparents sponsorship "},

    ]
  }
  ContinuedHandler = () => {
    this.props.history.replace('/about');   //on clickong continue path becomes....prev+'/contact' and hence according to the Route below it routes to contact data portion
  }

  render() {
    let cards=this.state.cards.map((card,id)=> {
      return(
        <div>
        <MDBCol md="4" >
           <div className='cards'>
            <MDBView waves>
           
              <img
              
                src={card.img}
                className="img-fluid card"
                alt=""
              /> 
              <MDBMask className="flex-center" overlay="teal-slight">
                <h4 className="white-text text"><span onClick={this.ContinuedHandler} className="textMargin" >{card.caption}  </span></h4>
              </MDBMask>

            </MDBView>
</div>
          </MDBCol>
          
            <Route path={this.props.match.path + '/about'} 
      component={About}/> 
      </div>
        )
    });
    return (
      <Fade bottom appear>
      <MDBContainer className="mt-5">
        <MDBRow className="mt-4">
        {cards}
        </MDBRow>
      </MDBContainer>
       
      </Fade>

    );
  }
}

export default PicCards;
